import React from 'react'
import logo from './logo.svg';
import './App.css';
import { Switch, Route } from "react-router-dom";
import { useState } from 'react';
import Home from "./Home.js";
import About from "./About.js";
import DeckList from "./DeckList.js"
import NavBar from "./NavBar.js"
import Search from "./Search.js"
import { card } from 'mtgsdk';

function App() {
const [userInput, setUserInput]=useState("")
const [results, setResults] = useState([])
const [deckList, setDeckList] = useState([])
const [addedCard, setAddedCard] = useState([])

function handleInputChange(e) {
setUserInput(e.target.value)
}


function handleSubmit(e) {
    e.preventDefault()
    fetch(`https://api.magicthegathering.io/v1/cards?name=${userInput}`)
    .then(prom=>prom.json())
    .then(data=> {console.log(results); setResults(data.cards)})
    setUserInput("")
  }

 function handleAddToDecklist(e) {
  fetch(`https://api.magicthegathering.io/v1/cards?name=${e.target.alt}`)
  .then(res => res.json())
  .then(data => {
    setAddedCard(data.cards.find(cardData => cardData.name === e.target.alt));
  })
console.log(addedCard)
  fetch('http://localhost:3000/decklist', {
      method: 'POST',
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json"
      },
      body: JSON.stringify(addedCard)
    })
 }

 function pullDeckList() {
  fetch('http://localhost:3000/decklist')
  .then(res=> res.json())
  .then(chosenCard => {
    if (deckList) {setDeckList(...chosenCard.cards, ...deckList)}
    else {setDeckList(chosenCard.deckList)}
  })
 }

  return (
    <div className="App">
      <NavBar />
      <Switch>
        <Route path="/about">
          <About />
        </Route>
        <Route path="/search">
          <Search handleAddToDecklist={handleAddToDecklist} results={results} handleSubmit={handleSubmit} userInput={userInput} handleChange={handleInputChange} setUserInput={setUserInput}/>
        </Route>
        <Route path="/decklist">
          <DeckList pullList={pullDeckList} deckList={deckList}/>
        </Route>
        <Route path="/">
          <Home />
        </Route>
      </Switch>
    </div>
  );
}

export default App;
