    <section className="slider">
        <FaArrowAltCircleLeft className='left-arrow' />
        <FaArrowAltCircleRight className='right-arrow' />
          {instructions.map((direction, index) => <h2 key={index}>{direction.sentence}</h2>)}
    </section>
    )
}

export default Home;
