import React from 'react'
import logo from './logo.svg';
import './App.css';
import { Switch, Route } from "react-router-dom";
import { useState, useEffect } from 'react';
import Home from "./Home.js";
import About from "./About.js";
import DeckList from "./DeckList.js"
import NavBar from "./NavBar.js"
import Search from "./Search.js"
import { card } from 'mtgsdk';
import { FaArrowAltCircleRight, FaArrowAltCircleLeft} from 'react-icons/fa'

function App() {
const [userInput, setUserInput]=useState("")
const [results, setResults] = useState([])
const [addedCard, setAddedCard] = useState([])
const [myDeckList, setMyDeckList] = useState([])


useEffect(() => {fetch('http://localhost:3000/decklist')
.then(res=>res.json())
.then(chosenCards => {
  console.log(chosenCards);
  if (myDeckList) {setMyDeckList([...chosenCards])}
})}, [addedCard]);

function handleInputChange(e) {
setUserInput(e.target.value)
}

function handleAddToDecklist(e) {
  fetch(`https://api.magicthegathering.io/v1/cards?name=${e.target.alt}`)
  .then(res => res.json())
  .then(data => {
    const validResults=data.cards.filter(cardData => cardData.imageUrl)
    setAddedCard(validResults.find(cardData => cardData.id === e.target.id));
    fetch('http://localhost:3000/decklist', {
      method: 'POST',
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json"
      },
      body: JSON.stringify(addedCard)
    })
  })
 }

function handleSubmit(e) {
    e.preventDefault()
    fetch(`https://api.magicthegathering.io/v1/cards?name=${userInput}`)
    .then(prom=>prom.json())
    .then(data=>  setResults(data.cards))
    setUserInput("")
  }


  return (
    <section className="slider">
      <NavBar />
      <Switch>
        <Route path="/about">
          <About />
        </Route>
        <Route path="/search">
          <Search handleAddToDecklist={handleAddToDecklist} results={results} handleSubmit={handleSubmit} userInput={userInput} handleChange={handleInputChange} setUserInput={setUserInput}/>
        </Route>
        <Route path="/decklist">
          <DeckList myDeckList={myDeckList} setMyDeckList={setMyDeckList} /*onRemove={handleRemoveCard}*/ />
        </Route>
        <Route path="/">
          <Home />
        </Route>
      </Switch>
    </section>
  );
}

export default App;
