import React from "react"
import {useEffect} from "react"

function DeckList( {pullList, deckList} ) {

useEffect(() => {fetch('http://localhost:3000/decklist')
.then(res=>res.json())
.then(chosenCards => {
  console.log(chosenCards)
  if (deckList) {setDeckList(...chosenCards, ...deckList)}
  else {setDeckList(chosenCards)}
})}, [deckList]);

const personalList= deckList.filter(result => result.imageUrl)+
console.log(deckList)

if (personalList){
    return (<div>
        {personalList.map((cardData, index)=> {

            return(
            <ul key={index}>
                <li>
                    <img alt={`${cardData.name}`} src={`${cardData.imageUrl}`} />
                    <p>Set: {cardData.setName}</p>
                </li>
            </ul>
        )})}
    </div>)}

    else {return (
        <div>
            <h2>Add to your Deck List from the Search!</h2>
        </div>
    )}
}

export default DeckList;
