import React from "react"
import {useState} from "react"


function DeckList( {myDeckList, setMyDeckList} ) {


       function handleRemoveCard (e) {
        fetch(`http://localhost:3000/decklist/${e.target.id}`, {
          method: 'DELETE',
          headers:{
            "content-type":"application/json"
          }
        })
        .then(res => res.json())
        .then(data => setMyDeckList([...myDeckList]))
       }


if (myDeckList) {
    const personalList= myDeckList.filter(result => result.imageUrl);
    return (<div>
        {personalList.map((cardData)=> {

            return(
            <ul key={cardData.id}>
                <li>
                    <img id={cardData.id} onClick={handleRemoveCard} alt={`${cardData.name}`} src={`${cardData.imageUrl}`} />
                    <p>Set: {cardData.setName}</p>
                </li>
            </ul>
        )})}
    </div>)}

    else {return (
        <div>
            <h2>Add to your Deck List from the Search!</h2>
        </div>
    )}
}

export default DeckList;
