import React from "react"
import {useEffect, useState} from "react"

function DeckList() {

const [myDeckList, setMyDeckList] = useState([])

useEffect(() => {fetch('http://localhost:3000/decklist')
.then(res=>res.json())
.then(chosenCards => {
  console.log(chosenCards);
  if (myDeckList) {setMyDeckList([...chosenCards, ...myDeckList])}
  else {setMyDeckList([chosenCards])}
})}, [personalList]);

console.log(myDeckList)

if (myDeckList) {
    const personalList= myDeckList.filter(result => result.imageUrl);
    return (<div>
        {personalList.map((cardData, index)=> {

            return(
            <ul key={index}>
                <li>
                    <img alt={`${cardData.name}`} src={`${cardData.imageUrl}`} />
                    <p>Set: {cardData.setName}</p>
                </li>
            </ul>
        )})}
    </div>)}

    else {return (
        <div>
            <h2>Add to your Deck List from the Search!</h2>
        </div>
    )}
}

export default DeckList;
